INSERT INTO `service` (`id`, `title`, `content`, `img`, `deadline`, `status`, `support`, `material`) VALUES
(1, 'Название услуги №1', '<p>Индекс технологической готовности – это объективный показатель, отражающий уровень организации производства на аудируемом предприятии, его готовность к технологическому перевооружению, а также возможности для инвестирования в инновационную деятельность или в мероприятия по модернизации.<br>В зависимости от результата оценки ИТГ предприятий МСП к развитию и внедрению инноваций определяются меры их поддержки: Высокий ИТГ - подготовка документов для инвесторов - предоставление грантов - предоставление гарантий Алтайского гарантийного фонда Средний ИТГ - проведение технологического аудита - разработка проектов модернизации - подготовка бизнес - планов - подготовка технико-экономического обоснования инвестиций Низкий ИТГ - предоставление нефинансовых форм поддержки - подготовка к участию ИТГ следующего года Порядок предоставления услуг 1.<br>Подача заявки на оказание услуг от предприятия МСП в КГБУ «Алтайский центр кластерного развития»2. Формирование перечня получателей господдержки Региональным центром инжиниринга в соответствии с Положением о предоставлении услуг3. Определение поставщиков услуг (инжиниринговых, консалтинговых компаний), в соответствии с Федеральным законом от 5.05.2013 №44-ФЗ «О контрактной системе в сфере закупок товаров, работ, услуг для обеспечения государственных и муниципальных нужд»4. Заключение контрактов между КГБУ «Алтайский центр кластерного развития» и выбранными поставщиками услуг на оказание последних в пользу МСП5. Исполнение договора инжиниринговой компанией6. Приемка оказанных услуг совместно КГБУ «Алтайский центр кластерного развития» и предприятием МСП7. Подготовка заключения со стороны предприятия МСП о качестве оказанной услуги8.</p>', '/web/uploads/9944_1531021343.jpg', '11.07.2018', 'Сбор заявок', '7.000.000 руб.', '/web/uploads/5354_1531709566.docx'),
(2, 'Название услуги №2', '<p>Индекс технологической готовности – это объективный показатель, отражающий уровень организации производства на аудируемом предприятии, его готовность к технологическому перевооружению, а также возможности для инвестирования в инновационную деятельность или в мероприятия по модернизации. </p><p>В зависимости от результата оценки ИТГ предприятий МСП к развитию и внедрению инноваций определяются меры их поддержки: Высокий ИТГ - подготовка документов для инвесторов - предоставление грантов - предоставление гарантий Алтайского гарантийного фонда Средний ИТГ - проведение технологического аудита - разработка проектов модернизации - подготовка бизнес - планов - подготовка технико-экономического обоснования инвестиций Низкий ИТГ - предоставление нефинансовых форм поддержки - подготовка к участию ИТГ следующего года Порядок предоставления услуг 1. </p><p>Подача заявки на оказание услуг от предприятия МСП в КГБУ «Алтайский центр кластерного развития»2. Формирование перечня получателей господдержки Региональным центром инжиниринга в соответствии с Положением о предоставлении услуг3. Определение поставщиков услуг (инжиниринговых, консалтинговых компаний), в соответствии с Федеральным законом от 5.05.2013 №44-ФЗ «О контрактной системе в сфере закупок товаров, работ, услуг для обеспечения государственных и муниципальных нужд»4. Заключение контрактов между КГБУ «Алтайский центр кластерного развития» и выбранными поставщиками услуг на оказание последних в пользу МСП5. Исполнение договора инжиниринговой компанией6. Приемка оказанных услуг совместно КГБУ «Алтайский центр кластерного развития» и предприятием МСП7. Подготовка заключения со стороны предприятия МСП о качестве оказанной услуги8. <br></p>', '/web/uploads/6931_1531289907.jpg', '14.08.2018', 'Сбор заявок', '200.000 руб.', ''),
(3, 'Название услуги №3', '<p>Индекс технологической готовности – это объективный показатель, отражающий уровень организации производства на аудируемом предприятии, его готовность к технологическому перевооружению, а также возможности для инвестирования в инновационную деятельность или в мероприятия по модернизации. </p><p>В зависимости от результата оценки ИТГ предприятий МСП к развитию и внедрению инноваций определяются меры их поддержки: Высокий ИТГ - подготовка документов для инвесторов - предоставление грантов - предоставление гарантий Алтайского гарантийного фонда Средний ИТГ - проведение технологического аудита - разработка проектов модернизации - подготовка бизнес - планов - подготовка технико-экономического обоснования инвестиций Низкий ИТГ - предоставление нефинансовых форм поддержки - подготовка к участию ИТГ следующего года Порядок предоставления услуг 1. </p><p>Подача заявки на оказание услуг от предприятия МСП в КГБУ «Алтайский центр кластерного развития»2. Формирование перечня получателей господдержки Региональным центром инжиниринга в соответствии с Положением о предоставлении услуг3. Определение поставщиков услуг (инжиниринговых, консалтинговых компаний), в соответствии с Федеральным законом от 5.05.2013 №44-ФЗ «О контрактной системе в сфере закупок товаров, работ, услуг для обеспечения государственных и муниципальных нужд»4. Заключение контрактов между КГБУ «Алтайский центр кластерного развития» и выбранными поставщиками услуг на оказание последних в пользу МСП5. Исполнение договора инжиниринговой компанией6. Приемка оказанных услуг совместно КГБУ «Алтайский центр кластерного развития» и предприятием МСП7. Подготовка заключения со стороны предприятия МСП о качестве оказанной услуги. <br></p>', '/web/uploads/705_1531289928.jpg', '14.08.2018', 'Конкурс', '4.000.000 руб.', ''),
(4, 'Консультация по вопросам оценки индекса технологической готовности ', '', '', '', '', '', '');

